mapper.insert:
insert into comment (commentId, type, productId, 
  content, score, commentTime
  )
values (#{commentId,jdbcType=CHAR}, #{type,jdbcType=CHAR}, #{productId,jdbcType=CHAR}, 
  #{content,jdbcType=VARCHAR}, #{score,jdbcType=TINYINT}, #{commentTime,jdbcType=CHAR}
  )

mapper.select:
select commentId, type, productId, content, score, commentTime
from comment
where commentId = #{commentId,jdbcType=CHAR}
