mapper.update:
update routetheme
set routeId = #{routeId,jdbcType=CHAR},
  themeId = #{themeId,jdbcType=CHAR}
where id = #{id,jdbcType=CHAR}

mapper.select:
select id, routeId, themeId
from routetheme
where id = #{id,jdbcType=CHAR}

select id, routeId, themeId
from routetheme
where routeId = #{routeId,jdbcType=CHAR}
